function App() {

  const [{ }, dispatch] = useStateValue();
  useEffect(() => {
    // This code will run once when the app component loads
    auth.onAuthStateChanged(authUser => {
      // console.log("Iam user" ,authUser);
      if (authUser) {
        // When user just Logged in
        dispatch({
          type: 'SET_USER',
          user: authUser
        })
      }
      else {
        // when user logged out
        dispatch({
          type: 'SET_USER',
          user: null
        })
      }
    })
  }, [])

  return (
    <Router>
      <div className="app">
        <Switch>
          <Route path="/login">
            <Login />
          </Route>
          <Route path="/checkout">
            <Header />
            <Checkout />
          </Route>
          <Route path="/payment">

            <Header />
            <Elements stripe={promise}>
            <Payment />

            </Elements>


          </Route>
          <Route path="/">
            <Header />
            <Home />
          </Route>
        </Switch>
      </div>
    </Router>


  );
}

export default App;
