export const initialState = {
    basket: [],
};
// Selector
export const getBasketTotal =(basket) =>{
    basket?.reduce((amout ,item)=>item.price + amount)
}


const reducer = (state, action) => {
    console.log(action ,"Chal rha hai")
    switch (action.type) {
        case 'ADD_TO_BASKET':
            return {
                ...state,
                basket: [...state.basket, action.item],
            };
        default:
            return state;
    }
    
};
export default reducer;
